import React, { useState, forwardRef } from 'react'
import './Post.css'
import { Avatar, Fade, Popper } from '@material-ui/core'
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';
import ChatBubbleOutlineIcon from '@material-ui/icons/ChatBubbleOutline';
import RepeatIcon from '@material-ui/icons/Repeat';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import SaveAltIcon from '@material-ui/icons/SaveAlt';
import EqualizerIcon from '@material-ui/icons/Equalizer';
import db from './firebase'
import moment from 'moment'
import 'moment/locale/es';
import Options from './Options';
import ModalPic from './ModalPic'

const link = "data:image/jpeg;base64,";

const Post = forwardRef(({
    displayName,
    username,
    verified,
    text,
    image,
    avatar,
    timestamp,
    id
}, ref) => {

    const [popper, setPopper] = useState(false);
    const [modal, setModal] = useState(false);

    const handleClose = () => {
        setModal(false);
    }

    const handleMouse = (event) => {
        setPopper(popper ? null : event.currentTarget);
    };

    const popperOpen = Boolean(popper);
    const popperid = popperOpen ? 'simple-popper' : undefined;

    const handleDelete = (id) => {

        db.collection('posts').doc(id).delete();
    }

    const getTime = (t) => {
        return moment(t).fromNow();
    }

    return (
        <div className="post" ref={ref}>
            <div className="post__avatar">
                <Avatar src={link || avatar} />
            </div>
            <div className="post__body">
                <div className="post__header">
                    <span>{displayName || "Default"}</span>
                    <span className="verified">{verified && <VerifiedUserIcon fontSize="small" />}</span>
                    <span className="username"> {"@" + username || "@default"}</span>
                    <span>·</span>
                    <span className="time"
                        aria-describedby={popperid}
                        onMouseOver={handleMouse}
                        onMouseLeave={handleMouse}>
                        {getTime(timestamp)}
                    </span>
                    <Popper transition id={popperid} open={popperOpen} anchorEl={popper} placement='bottom-start'>
                        {
                            ({ TransitionProps }) => (
                                <Fade {...TransitionProps} timeout={350}>
                                    <div className="popper">{moment(timestamp).format('lll')}</div>
                                </Fade>
                            )
                        }
                    </Popper>
                </div>

                <span className="threedots">
                    <Options handleDelete={handleDelete} id={id} />
                </span>

                <div className="post__text">
                    {text && text}
                </div>
                <div className="post__image">
                    {
                        image && 

                        <div>
                            <img style={{cursor:'pointer'}} src={image} alt="img" onClick={() => setModal(true)} />
                            <ModalPic image={image} modal={modal} handleClose={handleClose} />
                        </div>
                    }
                </div>
                <div className="post__buttons">
                    <ChatBubbleOutlineIcon />
                    <RepeatIcon />
                    <FavoriteBorderIcon />
                    <SaveAltIcon />
                    <EqualizerIcon />
                </div>
            </div>
        </div>
    )
})

export default Post